import {React, useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import logo from './../images/LogoMesto.svg';

function CurrentLink({dataLink}) {
    if (dataLink === "/sign-up") {
        return (<Link className="button-text" to="/sign-in">Войти</Link>)
    } else if (dataLink === "/sign-in") {
        return (<Link className="button-text" to="/sign-up">Регистрация</Link>)
    }}


function Header() {
    const [aa, setAa] = useState();
    useEffect(() => {setAa(window.location.pathname)}, [window.location.pathname])
    let currentRout = window.location.pathname;
    return (
        <header className="header">
            <img className="header__logo" src={logo} alt="Логотип"></img>
            <CurrentLink dataLink = {aa}/>
        </header >
    )
}

export default Header;
