import React from 'react';
import PopupWithForm from './PopupWithForm';

// Стейты, в которых содержятся значения инпута
const [name, setName] = useState('');
const [description, setDescription] = useState('');
const [validationMassegeName, setValidationMassegeName] = useState('');
const [validationMassegeDescription, setValidationMassegeDescription] = useState('');

// Обработчики изменения инпута - обновляет стейт
function handleChangeName(e) {
    setName(e.target.value);
    setValidationMassegeName(e.target.validationMessage);
}

function handleChangeDescription(e) {
    setDescription(e.target.value);
    setValidationMassegeDescription(e.target.validationMessage);
}


function Register() {
    return (

        <PopupWithForm
            name='popup_open popup__auth'
            text='Регистрация'
            titleColorControl='popup__title_auth'
            nameForm='popup__form_auth'
            buttonCloseDisable={true}
            buttonVisibleControl='popup__close-button_unvisibled'
            textButtonSubmit='Зарегистрироваться'
            colorButtonSubmit='popup__button_color'
            children={
                <>
                    <div className="popup__input-conainer popup__input-conainer_auth">
                        <input type="email" placeholder="Email" className="popup__input popup__input_auth"
                            id="username-input" minLength="2" required onChange={handleChangeName}
                        />
                        <span className="popup__text-error" id="error-firstname"></span>
                    </div>
                    <div className="popup__input-conainer popup__input-conainer_auth">
                        <input type="text" placeholder="Пароль" className="popup__input popup__input_auth"
                            id="username-input" minLength="2" required onChange={handleChangeDescription}
                        />
                        <span className="popup__text-error" id="error-firstname"></span>
                    </div>
                </>
            }


        />


    )
}

export default Register;
