import React from 'react';
import { Link, useParams } from 'react-router-dom';
import logo from './../images/LogoMesto.svg';

function currentLink() {
    if (currentLink === "/sign-up") {
        return <Link className="button-text" to="/sign-in">Войти</Link>
    } else if (currentLink === "/sign-in") {
        return <Link className="button-text" to="/sign-up">Регистрация</Link>
    }
}

function Header({ option }) {
    let currentRout = window.location.pathname;
    //console.log(currentRout)
    return (
        <header className="header">
            <img className="header__logo" src={logo} alt="Логотип"></img>
            currentLink
        </header >
    )
}

export default Header;
