        //     </div>
        // </div >

        <PopupWithForm
            name='popup_open popup__auth'
            text='Вход'
            titleColorControl='popup__title_auth'
            nameForm='popup__form_auth'
            buttonCloseDisable={true}
            buttonVisibleControl='popup__close-button_unvisibled'
            textButtonSubmit ='Войти'
            children={
                <>
                    <div className="popup__input-conainer">
                        <input type="email" placeholder="Email" className="popup__input popup__input_auth"
                            name="firstname" id="username-input" minLength="2"  required
                        />
                        <span className="popup__text-error" id="error-firstname"></span>
                    </div>
                    <div className="popup__input-conainer">
                        <input type="text" placeholder="Email" className="popup__input popup__input_auth"
                            name="firstname" id="username-input" minLength="2"  required
                        />
                        <span className="popup__text-error" id="error-firstname"></span>
                    </div>
                </>
            }


        />


    )
}

export default Login;
