import React, { useState, useEffect, useContext } from 'react';
import PopupWithForm from './PopupWithForm';

function EditAvatarPopup({isOpen, isClose}) {

    const inputRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();
        console.log(inputRef.current.value)
        // onUpdateAvatar({
        //     avatar: /* Значение инпута, полученное с помощью рефа */,
        // });
    }

    return (
        <PopupWithForm name='popup_avatar_redact'
            text='Обновить аватар'
            isOpen={isOpen}
            isClose={isClose}
            children={
                <input ref={inputRef} type="url" placeholder="Ссылка" className="popup__input popup__input_name_image"
                    name="link" id="ava-link" onChange={handleSubmit} required />
            }
        />
    )
}

export default EditAvatarPopup;
