import React, { useState } from 'react';
import PopupWithForm from './PopupWithForm';
import Successfully from './../images/Union successfully.svg'

function InfoTooltip({ name, isOpen, isActiveButtonSubmit, showButtonSubmit, children, buttonVisibleControl, isClose, buttonCloseDisable }) {
    return (
        <PopupWithForm
            isOpen={true}
            isActiveButtonSubmit={true}
            colorButtonSubmit={'popup__button_unvisible'} //спрятать кнопку сабмита
            titleColorControl = {''} // спрятать 
            children={
                <>
                    <div className='popup__container-info-tooltip'>
                        <img className='popup__img-info-tooltip' src={Successfully} />
                        <h2 className='popup__text-info-tooltip'>Вы успешно зарегистрировались!</h2>
                    </div>
                </>
            }
        />
    )
}

export default InfoTooltip;
