import React, { useState, useEffect } from 'react';
import api from './../../utils/Api';


function Main(props) {

    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');

    const [cards, setCards] = useState([]);

    // запрос данных пользователя с сервера
    useEffect(() => {
        api.getUserInfo().then((data) => {
            setUserName(data.name);
            setUserDescription(data.about);
            setUserAvatar(data.avatar);
        }).catch((err) => {
            console.log(err);
        })
    });

    //запрос карточек с сервера
    useEffect(() => {
        api.getImages().then((dataCards) => {
            //заготовка для изображения пользователя (карточки)
            dataCards.forEach((dataCard) => {
                //<!-- заготовка для изображения пользователя (карточки) -->
                <template class="work-piece">
                    <div class="element" id=".">
                        <img class="element__maskgroup" src="1" alt="1"/>
                        <div class="element__group">
                            <h2 class="element__title"></h2>
                            <div>
                                <button class="element__like" type="button"></button>
                                <h2 class="element__like-sum"></h2>
                            </div>
                            <button class="element__button-delete"></button>
                        </div>
                    </div>
                </template>

            })
        })
        });

        //{likes: Array(0), _id: '631a3ef6bd53430fc53207d7', name: 'kunica', link: 'https://placepic.ru/wp-content/uploads/2021/02/2-164.jpg', owner: {…}, …}
        return (
            <main>
                <section className="profile">
                    <div className="profile__container">
                        <img className="profile__avatar" src={userAvatar} alt="Аватар"></img>
                        <button className="profile__avatar-edit-button" type="button" onClick={props.onEditAvatar}></button>
                    </div>
                    <div className="profile__profile-info">
                        <div className="profile__content">
                            <h1 className="profile__firstname">{userName}</h1>
                            <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__subtext">{userDescription}</p>
                    </div>
                    <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
                </section>
                <section className="elements">
                    {/* заготовка для изображения пользователя (карточки) */}
                    <template className="work-piece">
                        <div className="element" id=".">
                            <img className="element__maskgroup" src="1" alt="1"></img>
                            <div className="element__group">
                                <h2 className="element__title"></h2>
                                <div>
                                    <button className="element__like" type="button"></button>
                                    <h2 className="element__like-sum"></h2>
                                </div>
                                <button className="element__button-delete"></button>
                            </div>
                        </div>
                    </template>
                </section>
            </main>
        )
    }

export default Main;
