import { React, useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import logo from './../images/LogoMesto.svg';

function CurrentLink({ dataLink }) {
    const history = useHistory();
    const [currentRout, setCurrentRout] = useState('');
    useEffect(() => {
        return history.listen((location) => {
            setCurrentRout(location.pathname);
        })
    }, [history])

    if (currentRout === "/sign-up") {
        return (<Link className="button-text" to="/sign-in">Войти</Link>)
    } else if (currentRout === "/sign-in") {
        return (<Link className="button-text" to="/sign-up">Регистрация</Link>)
    }
}


function Header() {
    let currentRout = window.location.pathname;
    // useEffect(() => console.log(window.location));
    return (
        <header className="header">
            <img className="header__logo" src={logo} alt="Логотип"></img>
            <CurrentLink dataLink={currentRout} />
        </header >
    )
}

export default Header;
