import React, { useState, useEffect } from 'react';
import api from './../../utils/Api';


function Main(props) {

    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');


    useEffect(() => {
        api.getUserInfo().then((data) => { 
            setUserName(data.name);
            setUserDescription(data.about);
            setUserAvatar(data.avatar);
            console.log(data.avatar)
         }).catch((err) => {
            console.log(err);
        }) 
      });

    return (
        <main>
            <section className="profile">
                <div className="profile__container">
                    <img className="profile__avatar" src="${userAvatar})`" style={{ backgroundImage: `url(${userAvatar})` }}  alt="Аватар"></img>
                    <button className="profile__avatar-edit-button" type="button" onClick={props.onEditAvatar}></button>
                </div>
                <div className="profile__profile-info">
                    <div className="profile__content">
                        <h1 className="profile__firstname">Борат</h1>
                        <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__subtext">Бешгеренмист</p>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>
            <section className="elements">
                {/* заготовка для изображения пользователя (карточки) */}
                <template className="work-piece">
                    <div className="element" id=".">
                        <img className="element__maskgroup" src="1" alt="1"></img>
                        <div className="element__group">
                            <h2 className="element__title"></h2>
                            <div>
                                <button className="element__like" type="button"></button>
                                <h2 className="element__like-sum"></h2>
                            </div>
                            <button className="element__button-delete"></button>
                        </div>
                    </div>
                </template>
            </section>
        </main>
    )
}

export default Main;
