import React, { useState } from 'react';
import Header from './Header/Header';
import Main from './Main/Main';
import Footer from './Footer/Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';


function handleEditAvatarClick() {
  document.querySelector('.popup_avatar_redact').classList.add('popup_open')
}

const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(0);

function handleEditProfileClick() {
  document.querySelector('.popup_user_input').classList.add('popup_open')
}

const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(0);

function handleAddPlaceClick() {
  document.querySelector('.popup_image_content').classList.add('popup_open')
}

const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(0);

function App() {
  return (
    <div className="page">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} />
      <Footer />
      {/* <!--Попап Редактирование профиля --> */}
      <PopupWithForm name='popup_user_input' text='Редактировать профиль' />
      {/* <!--Попап добавление изображений пользователем --> */}
      <PopupWithForm name='popup_image_content' text='Новое место' />
      {/* <!-- Попап подтверждения удаления карточки --> */}
      <PopupWithForm name='popup_card_remove' text='Вы уверены?' />
      {/* <!--Попап форма редактирования аватара --> */}
      <PopupWithForm name='popup_avatar_redact' text='Обновить аватар' />
      {/* <!--Попап Масштабированное изображение --> */}
      <ImagePopup />
    </div>
  );
}

export default App;
