import React from 'react';
import PopupWithForm from './PopupWithForm';
import Successfully from './../images/Union successfully.svg'
import Unsuccessfully from './../images/Union unsuccessful.svg'

function InfoTooltip({ isRegister, isClose, isAuth }) {
    console.log(isAuth, isRegister)


    return (
        <PopupWithForm
            isOpen={isRegister || isAuth ? true : false}
            isClose={isClose}
            isActiveButtonSubmit={isRegister}
            colorButtonSubmit={'popup__button_unvisible'} //спрятать кнопку сабмита
            titleColorControl={'popup__title_unvisible'} // спрятать заголовок формы, убрать отступы
            children={
                <>
                    <div className='popup__container-info-tooltip'>
                        {/* <img className='popup__img-info-tooltip' src={isAuth ? Unsuccessfully : Successfully} />
                        <h2 className='popup__text-info-tooltip'>{
                            isAuth ? 'Что-то пошло не так! Попробуйте ещё раз.'
                                : 'Вы успешно зарегистрировались! Перейдите на вкладку Войти'
                        }</h2> */}
                        {isAuth ? <>
                            <img className='popup__img-info-tooltip' src={Unsuccessfully} />
                            <h2 className='popup__text-info-tooltip'>'Что-то пошло не так! Попробуйте ещё раз.'</h2>
                        </>

                            : ''}
                        {isRegister ? <> <img className='popup__img-info-tooltip' src={Successfully} />
                        </>
                         : ''}
                    </div>
                </>
            }
        />
    )
}


export default InfoTooltip;
