import React, { useState } from 'react';
import Header from './Header/Header';
import Main from './Main/Main';
import Footer from './Footer/Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';


function App() {

// функции открытия попапов

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen('popup_open')
  }

  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState('');


  function handleEditProfileClick() {
    setEditProfilePopupOpen('popup_open')
  }

  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);

  function handleAddPlaceClick() {
    setAddPlacePopupOpen('popup_open')
  }

  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);

  function handleDeletePlaceClick() {
    setDeletePlace('popup_open');
  }

  const [isDeletePlacePopupOpen, setDeletePlace] = useState('');

  // функция закрытия попапов

  function closeAllPopups () {
    console.log(2)
  }

  return (
    <div className="page">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} />
      <Footer />
      {/* <!--Попап Редактирование профиля --> */}
      <PopupWithForm name='popup_user_input' text='Редактировать профиль' isOpen = {isEditProfilePopupOpen} isClose = {$closeAllPopups} />
      {/* <!--Попап добавление изображений пользователем --> */}
      <PopupWithForm name='popup_image_content' text='Новое место' isOpen = {isAddPlacePopupOpen}/>
      {/* <!-- Попап подтверждения удаления карточки --> */}
      <PopupWithForm name='popup_card_remove' text='Вы уверены?' isOpen = {isDeletePlacePopupOpen}/>
      {/* <!--Попап форма редактирования аватара --> */}
      <PopupWithForm name='popup_avatar_redact' text='Обновить аватар' isOpen = {isEditAvatarPopupOpen}/>
      {/* <!--Попап Масштабированное изображение --> */}
      <ImagePopup />
    </div>
  );
}

export default App;
