    return (

        <PopupWithForm
            name='popup_open popup__auth'
            text='Регистрация'
            titleColorControl='popup__title_auth'
            nameForm='popup__form_auth'
            buttonCloseDisable={true}
            buttonVisibleControl='popup__close-button_unvisibled'
            textButtonSubmit='Зарегистрироваться'
            colorButtonSubmit='popup__button_color'
            children={
                <>
                    <div className="popup__input-conainer popup__input-conainer_auth">
                        <input type="email" placeholder="Email" className="popup__input popup__input_auth"
                            id="username-input" minLength="2" required onChange={handleChangeName}
                        />
                        <span className="popup__text-error" id="error-firstname">{validationMassegeName}</span>
                    </div>
                    <div className="popup__input-conainer popup__input-conainer_auth">
                        <input type="text" placeholder="Пароль" className="popup__input popup__input_auth"
                            id="username-input" minLength="2" required onChange={handleChangeDescription}
                        />
                        <span className="popup__text-error" id="error-firstname">{validationMassegeDescription}</span>
                    </div>
                </>
            }


        />


    )
}

export default Register;
